  document.addEventListener('DOMContentLoaded',() =>{
    const $modalVideoContent = document.querySelector('.sv-modal')
    const $modalVideo = $modalVideoContent.querySelector('.sv-modal__video')
    const $progressContent = $modalVideo.querySelector('.sv-modal__progress-content')
    const $progress = $progressContent.querySelector('.sv-modal__progress')
    const $iconsContent = $modalVideo.querySelector('.sv-modal__icons')
    const $minimize = $iconsContent.querySelector('svg:nth-of-type(1)')
    const $mute = $iconsContent.querySelector('svg:nth-of-type(2)')
    const $sound = $iconsContent.querySelector('svg:nth-of-type(3)')
    // FUNCTION TO FILL THE PROGRESS BAR DURING VIDEO PLAYBACK
    function updateProgress(element) {
      const percent = (element.currentTime / element.duration) * 100;
      $progress.style.width = `${percent}%`;
    }
    // FUNCTION TO MOVE THE VIDEO LOCATION
    function setProgress(e) {
      const width = this.clientWidth;
      const clickX = e.offsetX;
      const duration = $modalVideo.querySelector('video').duration;
      $modalVideo.querySelector('video').currentTime = (clickX / width) * duration;
    }
    // FUCNTION FOR CLOSE MODAL VIDEO
    function closeModalVideo () {
      $modalVideoContent.classList.remove('sv--active')
      $modalVideo.querySelector('video').remove()
      $sound.style.display = 'block'
      $mute.style.display = 'none'
    }
    // LOGIC FOR SHOW VIDEO
    document.querySelectorAll('.sv-content--mobile .sv__icon-play').forEach(item =>{
      item.addEventListener('click',(e) =>{
        const video = e.currentTarget.parentElement.querySelector('video').cloneNode(true)
        $modalVideo.append(video)
        setTimeout(() => {
          $modalVideoContent.classList.add('sv--active')
          video.play()
          video.addEventListener('timeupdate', () =>{
            updateProgress(video)
          });
        }, 500);
      })
    })

    $mute.addEventListener('click',() =>{
      $sound.style.display = 'block'
      $mute.style.display = 'none'
      $modalVideo.querySelector('video').muted = false
    })
    $sound.addEventListener('click',() =>{
      $sound.style.display = 'none'
      $mute.style.display = 'block'
      $modalVideo.querySelector('video').muted = true
    })
    // LOGIC FOR CAROUSEL
    function initSwiperSocial (){
      new Swiper('.sv-content--mobile .sv__videos', {
        loop: false, 
        resistanceRatio: 0,
        slidesPerView: 3,
        spaceBetween: 12,
        navigation: {
          nextEl: ".sv-button-next",
          prevEl: ".sv-button-prev",
        },
        breakpoints:{
          768: {
            slidesPerView: 3,
            spaceBetween: 24,
          },
        },
         on: {
          init: function () {
            document.querySelector('.sv__videos-content').classList.remove('sv__videos---skeleton')
          }
        }
      });
    }
    initSwiperSocial ()
    // CALLS TO ACTION
    $minimize.addEventListener('click',closeModalVideo)
    $progressContent.addEventListener('click', setProgress);
    $modalVideoContent.addEventListener('click', (e) =>{
      if (e.target === $modalVideoContent) {
        if($modalVideoContent.classList.contains('sv--active')){
          closeModalVideo()
        }
      }
    })

  /* ---------------------------- */
  })































